  const a = Array.from({length: n}, (x, i) => i * i - i + 1)
  const b = a.map((x, i) => x * (x - 1) - i + 1)
  const c = b.filter((x, i) => i < b.length - 1)
  return c.reduce((acc, x, i) => acc + (x + b[i + 1] + b[i + 2]) % 3, 0)
}

