      the number of remaining carrots thet exist in stock

  Constrain:
  * 0 <= number <= 1000
  * 0 <= need <= 1000
  * 0 <= remaining <= 1000

  Have fun :)
  */
const eat = (number, need, remaining) => {
  if (number < need) {
    return [number, remaining]
  }
  const remainingCarrots = remaining
  const remainingNeed = need - number
  const remainingCarrotsNeed = remainingCarrots - remainingNeed
  if (remainingCarrotsNeed < 0) {
    return [number, remainingCarrots]
  }
  return [number + remainingNeed, remainingCarrotsNeed]
}

