from typing import List


def all_prefixes(string: str) -> List[str]:
    # Return list of all prefixes from shortest to longest of the input string
    # >>> all_prefixes('abc')
    # ['a', 'ab', 'abc']
    result = []

    for i in range(1, len(string) + 1):
        if string[:i] not in result:
            result.append(string[:i])

    return result


def longest_prefix(string: str) -> str:
    # Return longest prefix of the input string
    # >>> longest_prefix('abc')
    # 'abc'
    return string[:len(string) - 1]


def longest_common_prefix(strings: List[str]) -> str:
    # Return longest common prefix of the input strings
    # >>> longest_common_prefix(['abc', 'abd', 'abce'])
    # 'ab'
    return ''.join(s[:len(s) - 1] for s in strings)


