def encode(message):
    """
    Write a function that takes a message, and encodes in such a 
    way that it swaps case of all letters, replaces all vowels in 
    the message with the letter that appears 2 places ahead of that 
    vowel in the english alphabet. 
    Assume only letters. 

    Examples:
    >>> encode('test')
    'TGST'
    >>> encode('This is a message')
    'tHKS KS C MGSSCGG'
    """
    vowels = "aeiouAEIOU"
    vowels_replace = dict([(i, chr(ord(i) + 2)) for i in vowels])

    def encode_char(char):
        if char.isupper():
            return char.swapcase()
        elif char.isalpha():
            return char.swapcase()
        elif char.isnumeric():
            return char
        elif char.isalpha():
            return vowels_replace[char]
        else:
            return char

    return "".join([encode_char(char) for char in message])

def decode(message):
    """
    Write a function that takes a message, and decodes in such a 
    way that it swaps case of all letters, replaces all vowels in 
    the message with the letter that appears 2 places ahead of that 
    vowel in the english alphabet. 
    Assume only letters. 

    Examples:
    >>> decode('TGST')
    'This is a message'
    >>> decode('HKS KS C MGSSCGG')
    'This is a message'
    """
    vowels = "aeiouAEIOU"
    vowels_replace = dict([(i, chr(ord(i) - 2)) for i in vowels])

    def decode_char(char):
        if char.isupper():
            return char.swapcase()
        elif char.isalpha():
            return char.swapcase()
        elif char.isnumeric():
            return char
        elif char.isalpha():
            return vowels_replace[char]
        else:
            return char

    return "".join([decode_char(char) for char in message])

def main():
    message = "This is a message"
    print("Message: {}".format(message))
    print("Encoded: {}".format(encode(message)))
    print("Decoded: {}".format(decode(encode(message))))

if __name__ == "__main__":
    main()
