def string_to_md5(text):
    """
    Given a string 'text', return its md5 hash equivalent string.
    If 'text' is an empty string, return None.

    >>> string_to_md5('Hello world') == '3e25960a79dbc69b674cd4ec67a72c62'
    """
    import hashlib

    if text:
        return hashlib.md5(text).hexdigest()
    else:
        return None

def md5_to_string(md5_hash):
    """
    Given a md5 hash'md5_hash', return its string equivalent.
    If'md5_hash' is None, return None.

    >>> md5_to_string('3e25960a79dbc69b674cd4ec67a72c62') == 'Hello world'
    """
    import hashlib

    if md5_hash:
        return hashlib.md5(md5_hash).hexdigest()
    else:
        return None

def get_md5_from_file(file_path):
    """
    Given a file path 'file_path', return its md5 hash equivalent string.
    If 'file_path' is None, return None.

    >>> get_md5_from_file('/tmp/test.txt') == '3e25960a79dbc69b674cd4ec67a72c62'
    """
    import hashlib

    if file_path:
        with open(file_path, 'rb') as f:
            return hashlib.md5(f.read()).hexdigest()
    else:
        return None

