    """
    Given a file path 'file_path', return its md5 hash equivalent string.
    If 'file_path' is None, return None.

    >>> get_md5_from_file_with_size('/tmp/test.txt', 1024) == '3e25960a79dbc69b674cd4ec67a72c62'
    """
    import hashlib

    if file_path:
        with open(file_path, 'rb') as f:
            return hashlib.md5(f.read(size)).hexdigest()
    else:
        return None

