from typing import List


def string_xor(a: str, b: str) -> str:
    """ Input are two strings a and b consisting only of 1s and 0s.
    Perform binary XOR on these inputs and return result also as a string.
    >>> string_xor('010', '110')
    '100'
    """

    def xor(i, j):
        if i == j:
            return '0'

        if i == '1':
            return '0'

        if j == '1':
            return '1'

        return 'X'

    return ''.join(map(xor, a, b))


def string_xor_recursive(a: str, b: str) -> str:
    """ Input are two strings a and b consisting only of 1s and 0s.
    Perform binary XOR on these inputs and return result also as a string.
    >>> string_xor_recursive('010', '110')
    '100'
    """

    if len(a) == 0 or len(b) == 0:
        return ''

    if a == b:
        return '0'

    return ''.join(map(lambda x: xor(x, b[0]), a)) + string_xor_recursive(a[1:], b[1:])


def string_xor_iterative(a: str, b: str) -> str:
    """ Input are two strings a and b consisting only of 1s and 0s.
    Perform binary XOR on these inputs and return result also as a string.
    >>> string_xor_iterative('010', '110')
    '100'
    """

    def xor(i, j):
        if i == j:
            return '0'

        if i == '1':
            return '0'

        if j == '1':
            return '1'

        return 'X'

    return ''.join(map(xor, a, b))


