    seen = set()
    seen_add = seen.add
    return [x for x in l if x not in seen and not seen_add(x)]

def unique_everseen(l: list, key=None) -> list:
    """Return an iterator of elements that are always in the order
    they are first seen.
    >>> list(unique_everseen([2,4,6,4,2,3,4,3,5,6]))
    [2, 3, 4, 4, 5, 6]
