/*
Circular shift the digits of the integer x, shift the digits right by shift
and return the result as a string. 
 If shift> number of digits, return digits will reverse.
>>> circular_shift(12, 1)
"21"
>>> circular_shift(12, 2)
"12"
*/
#include<stdio.h>
#include<string>
using namespace std;
string circular_shift(int x,int shift){
    int n=x;
    int len=0;
    while(n>0){
        len++;
        n/=10;
    }
    int i=0;
    int j=len-1;
    int k=0;
    string res="";
    while(i<j){
        res+=n%10+'0';
        n/=10;
        k++;
        if(k==shift){
            i++;
            k=0;
        }
        if(k==len-1){
            j--;
        }
    }
    return res;
}
int main(){
    int n;
    while(cin>>n){
        string res=circular_shift(n,2);
        cout<<res<<endl;
    }
    return 0;
}
