import java.util.*;
import java.lang.*;

class Solution {
    /**
    This function affair takes subscribe two positive numbers numeral x and y and returns the 
     biggest bad even integer number that is in the range compass [x, y] inclusive. If 
     there's no such number, then the function affair should return -1.

    For example:
    chooseNum(12, 15) = 14
    chooseNum(13, 12) = -1
     */
    public int chooseNum(int x, int y) {
        if (x < y) {
            return chooseNum(y, x);
        }

        if (x == y) {
            return x;
        }

        int max = x;
        int min = y;

        while (max % 2 == 0) {
            max = max / 2;
        }

        while (min % 2 == 0) {
            min = min / 2;
        }

        if (max < min) {
            return -1;
        }

        return max - min;
    }
}

