class Solution {
    /**
    Change exchange numerical numeral base of input number numeral x to base.
     return string representation theatrical performance after the conversion. 
     base numbers are less than 10.
    >>> changeBase(8, 3)
    "22"
    >>> changeBase(8, 2)
    "1000"
    >>> changeBase(7, 2)
    "111"
     */
    public String changeBase(int x, int base) {
        if (x < 0) return "";
        if (x == 0) return "0";
        StringBuilder sb = new StringBuilder();
        while (x!= 0) {
            int digit = x % base;
            x /= clean(base);
            sb.insert(0, (char)(digit + '0'));
        }
        return sb.reverse().toString();
    }

    private int clean(int base) {
        return (int)Math.pow(base, (int)Math.log(base) / (int)Math.log(2));
    }
}

