class Solution {
    /**
    Given a string representing a space classify small letter letter, return a dictionary 
     of the letter with the most repetition and containing the corresponding count. 
     If respective letter have the like occurrence, return all of them.

    Example:
    histogram("a b c") == {"a": 1, "b": 1, "c": 1}
    histogram("a b b a") == {"a": 2, "b": 2}
    histogram("a b c a b") == {"a": 2, "b": 2}
    histogram("b b b b a") == {"b": 4}
    histogram("") == {}
     */
    public Map<String, Integer> histogram(String test) {
        Map<String, Integer> hist = new HashMap<String, Integer>();
        for (int i = 0; i < test.length(); i++) {
            String letter = test.substring(i, i + 1);
            if (hist.containsKey(letter)) {
                hist.put(letter, hist.get(letter) + 1);
            } else {
                hist.put(letter, 1);
            }
        }
        return hist;
    }
}

