        }
        int max = 0;
        for (int k = 0; k < grid.get(i).get(j); k++) {
            int new_max = dfs(grid, dp, i + 1, j, capacity - k) + dfs(grid, dp, i, j + 1, capacity - k);
            if (new_max > max) {
                max = new_max;
            }
        }
        dp[i][j] = max;
        return max;
    }

