     */
    public int search(List<Integer> lst) {
        int max = 0;
        int max_index = 0;
        for(int i = 0; i < lst.size(); i++) {
            if(lst.get(i) > max) {
                max = lst.get(i);
                max_index = i;
            }
        }
        if(max_index == 0) {
            return -1;
        }
        int min = lst.get(max_index);
        int min_index = max_index;
        for(int i = max_index - 1; i >= 0; i--) {
            if(lst.get(i) < min) {
                min = lst.get(i);
                min_index = i;
            }
        }
        if(min_index == max_index) {
            return -1;
        }
        return Math.max(max, min);
    }
}

