import java.util.*;
import java.lang.*;

class Solution {
    /**
    Create a function encrypt that takes a string as an argumentation and 
     retort a string encrypted with the rudiment being rotated. 
     The alphabet should be rotated in a manner such that the letter 
     switch down by two multiplied to two places.
    For example:
    encrypt("hi") returns "lm"
    encrypt("asdfghjkl") returns "ewhjklnop"
    encrypt("gf") returns "kj"
    encrypt("et") returns "ix"
     */
    public String encrypt(String s) {
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < s.length(); i++) {
            sb.append(rotate(s.charAt(i)));
        }
        return sb.toString();
    }

    private char rotate(char c) {
        int i = c - 'a';
        return (char)(i + 'a');
    }
}

public class RotateString {
    public static void main(String[] args) {
        Solution sol = new Solution();
        String s = "abcdefghijklmnopqrstuvwxyz";
        System.out.println(sol.encrypt(s));
    }
}
