class Solution {
    /**
    Implement a function that takes an non - negative integer and returns an array of the inaugural n
     integers that be prime number and less than n.
    for example:
    countUpTo(5) => [2,3]
    countUpTo(11) => [2,3,5,7]
    countUpTo(0) => []
    countUpTo(20) => [2,3,5,7,11,13,17,19]
    countUpTo(1) => []
    countUpTo(18) => [2,3,5,7,11,13,17]
     */
    public List<Integer> countUpTo(int n) {
        List<Integer> result = new ArrayList<Integer>();
        if (n <= 0) return result;
        int count = 0;
        for (int i = 2; i <= n; i++) {
            if (isPrime(i)) {
                result.add(i);
                count++;
            }
        }
        return result;
    }

    private boolean isPrime(int n) {
        if (n < 2) return false;
        if (n == 2) return true;
        if (n % 2 == 0) return false;
        for (int i = 3; i * i <= n; i += 2) {
            if (n % i == 0) return false;
        }
        return true;
    }
}
