def rot13(c: str) -> str:
    """
    Rotates character by 13 places.
    """
    if c.isalpha():
        if c.isupper():
            return chr(ord(c.upper()) + 13)
        else:
            return chr(ord(c.lower()) + 13)
    elif c.isdigit():
        return chr(ord(c) + 13)
    else:
