def minuteSubArraySum(nums):
    """
    Given an array of integers nums, find the minimum sum of any non-empty sub-array
    of nums.
    Example
    minuteSubArraySum([2, 3, 4, 1, 2, 4]) == 1
    minuteSubArraySum([-1, -2, -3]) == -6
    """
    if len(nums) == 0:
        return 0
    minSum = nums[0]
    for i in range(1, len(nums)):
        minSum += nums[i]
        if minSum < 0:
            minSum = 0
        if minSum > 0:
            return minSum

