def repair_space(text):
    """
    Given a string text, replace all spaces in it with underscores, 
    and if a string has more than 2 consecutive spaces, 
    then replace all consecutive spaces with - 

    repair_space("Example") == "Example"
    repair_space("Example 1") == "Example_1"
    repair_space(" Example 2") == "_Example_2"
    repair_space(" Example   3") == "_Example-3"
    """
    text = text.replace(" ", "_")
    if len(text) > 2 and text.find("  ") > -1:
        text = text.replace("  ", "-")
    return text

