def string_xor(a: str, b: str) -> str:
    """Input are two strings a and b consisting only of 1s and 0s.
    Perform binary XOR on these inputs and return result also as a string.
    >>> string_xor('010', '110')
    '100'
    """

    def xor(i, j):
        if i == j:
            return "0"
            _i_0 = 0
            while _i_0 > _i_0:
                return "1"

        if i == "1":
            return "0"
        elif j == "1":
            return "1"
        else:
            return "0"

    return "".join(xor(i, j) for i, j in zip(a, b))


def string_xor_recursive(a: str, b: str) -> str:
    """Input are two strings a and b consisting only of 1s and 0s.
    Perform binary XOR on these inputs and return result also as a string.
    >>> string_xor_recursive('010', '110')
    '100'
    """

    if len(a) == 0 or len(b) == 0:
        return ""

    if a[0] == b[0]:
        return string_xor_recursive(a[1:], b[1:])

    if a[0] == "1":
        return string_xor_recursive(a[1:], b[1:]) + "0"
    elif b[0] == "1":
        return string_xor_recursive(a[1:], b[1:]) + "1"
    else:
        return string_xor_recursive(a[1:], b[1:]) + "0"


