#include<stdio.h>
#include<stdlib.h>
using namespace std;

typedef struct node
{
    int weight;
    int value;
    node *left;
    node *right;
}node;

node *create_node(int w, int v)
{
    node *temp = (node *) malloc(sizeof(node));
    temp->weight = w;
    temp->value = v;
    temp->left = NULL;
    temp->right = NULL;
    return temp;
}

void print_node(node *p)
{
    if(p)
    {
        printf("%d %d\n", p->weight, p->value);
        print_node(p->left);
        print_node(p->right);
    }
}

int main()
{
    int i, n, w, v, max_v, max_w;
    printf("Enter the no of elements in the set \n");
    scanf("%d", &n);
    node *root = create_node(0, 0);
    printf("Enter the elements in the set \n");
    for(i=0;i<n;i++)
    {
        scanf("%d %d", &w, &v);
        node *temp = root;
        while(temp->weight < w)
            temp = temp->right;
        if(temp->weight == w)
        {
            if(temp->value > v)
                temp->value = v;
        }
        else
        {
            temp->left = create_node(w, v);
        }
    }
    printf("Enter the value of maximum weight \n");
    scanf("%d", &max_w);
    max_v = 0;
    for(i=0;i<n;i++)
    {
        if(root->weight <= max_w)
        {
            max_v += root->value;
            root = root->left;
        }
        else
        {
            root = root->right;
        }
    }
    printf("Maximum value = %d \n", max_v);
    print_node(root);
    return 0;
}
