#include<stdio.h>
#include<vector>
using namespace std;
vector<int> genEratE_inteGerS(int a,int b){
    vector<int> res;
    int i = 0;
    int ai = a;
    int bi = b;
    while(ai <= bi){
        if(ai % 2 == 0){
            res.push_back(ai);
            ai += 2;
        }
        else{
            ai++;
        }
    }
    return res;
}
