Given a string representing a space separated lowercase letters, return a map
of the letter with the most repetition and containing the corresponding count.
If several letters have the same occurrence, return all of them.

Example:
histograms("a b c") == {{"a", 1}, {"b", 1}, {"c", 1}}
histograms("a b b a") == {{"a", 2}, {"b", 2}}
histograms("a b c a b") == {{"a", 2}, {"b", 2}}
histograms("b b b b a") == {{"b", 4}}
histograms("") == {}

*/
#include<stdio.h>
#include<string>
#include<map>
using namespace std;
map<char,int> histograms(string test){
    map<char,int> hist;
    for(int i=0;i<test.size();i++){
        hist[test[i]]++;
    }
    return hist;
}
int main(){
    string test;
    cin>>test;
    map<char,int> hist=histograms(test);
    map<char,int>::iterator it;
    int max=0,count=0;
    for(it=hist.begin();it!=hist.end();it++){
        if(it->second>max){
            max=it->second;
            count=1;
        }else{
            count++;
        }
    }
    cout<<"The "<<max<<" most frequent letter is "<<hist.begin()->first<<" "<<count<<endl;
    return 0;
}
