of nums.
Example
minSubsArraySums({2, 3, 4, 1, 2, 4}) == 1
minSubsArraySums({-1, -2, -3}) == -6
*/
#include<stdio.h>
#include<vector>
using namespace std;
long long minSubsArraySums(vector<long long> nums){

canonical_solution:
    long long current,min;
    current=nums[0];
    min=nums[0];
    for (int i=1;i<nums.size();i++)
    {
       if (current<0) current=current+nums[i];
       else current=nums[i];
       if (current<min) min=current;
    }
    return min;
}

