and if a string has more than 2 consecutive spaces, 
then replace all consecutive spaces with - 

fixes_space("Example") == "Example"
fixes_space("Example 1") == "Example_1"
fixes_space(" Example 2") == "_Example_2"
fixes_space(" Example   3") == "_Example-3"
*/
#include<stdio.h>
#include<string>
using namespace std;
string fixes_space(string text){
	string::size_type first = text.find_first_of(" ");
	string::size_type last = text.find_last_of(" ");
	string result = text;
	if(first!= string::npos && last!= string::npos){
		result.replace(first, 1, "_");
		result.replace(last, 1, "_");
		if(last - first > 2){
			result.replace(last - 2, 3, "-");
		}
	}
	return result;
}
int main(){
	string text;
	getline(cin, text);
	cout<<fixes_space(text)<<endl;
	return 0;
}
