can_arrange({1,2,4,3,5}) = 3
can_arrange({1,2,3}) = -1
*/
#include<stdio.h>
#include<vector>
using namespace std;
int can_arrange(vector<int> arr){
    int start = 0;
    int end = arr.size() - 1;
    int mid = 0;
    int mid_val = 0;
    while(start < end){
        mid = (start + end) / 2;
        mid_val = arr[mid];
        if(mid_val < arr[mid + 1])
            start = mid + 1;
        else
            end = mid;
    }
    return start == arr.size() - 1? start : -1;
}
