vector = {2, 3, 4, 5}
result = 2 + 3 * 4 - 5
=> result = 9

Note:
    The length of operator vector is equal to the length of operand vector minus one.
    Operand is a vector of of non-negative integers.
    Operator vector has at least one operator, and operand vector has at least two operands.

*/
#include<stdio.h>
#include<math.h>
#include<vector>
#include<string>
using namespace std;
#include<algorithm>
#include<stdlib.h>
int do_algebra(vector<string> operato, vector<int> operand){
	int result = 0;
	for(int i = 0; i < operato.size()-1; i++){
		for(int j = 0; j < operand.size()-1; j++){
			if(operato[i] == "+"){
				result = result + operand[j];
			}
			if(operato[i] == "-"){
				result = result - operand[j];
			}
			if(operato[i] == "*"){
				result = result * operand[j];
			}
			if(operato[i] == "/"){
				result = result / operand[j];
			}
			if(operato[i] == "//"){
				result = result // operand[j];
			}
			if(operato[i] == "^"){
				result = result * pow(operand[j], 2);
			}
			if(operato[i] == "**"){
				result = result * pow(operand[j], 1);
			}
			if(operato[i] == "="){
				result = operand[j];
			}
		}
	}
	return result;
}
int main(){
	int t;
	vector<string> operato;
	vector<int> operand;
	vector<int> operand_aux;
	vector<int> result;
	cin >> t;
	while(t--){
		cin >> operato;
		cin >> operand;
		operand_aux = operand;
		operand_aux.erase(operand_aux.begin());
		result = do_algebra(operato, operand);
		for(int i = 0; i < result.size(); i++){
			cout << result[i] << endl;
		}
	}
	return 0;
}
