#include<math.h>
using namespace std;
float truncate_number(float number){
    long long int decimals = number*10.0;
    long long int numerator = decimals;
    long long int divisor = 1;
    long long int remainder = 0;
    int index = 0;
    int i = 0;
    while(numerator/divisor!= remainder){
        remainder = numerator%divisor;
        index = i;
        i++;
    }
    index++;
    float decimal_part = number - numerator/divisor;
    return decimal_part/10.0;
}

