import java.lang.*;

class Solution {
    /**
    Input to this function is a string containing multiple groups of nested parentheses. Your goal is to
    separate those group into separate strings and return the list of those.
    Separate groups are balanced (each open brace is properly closed) and not nested within each other
    Ignore any spaces in the input string.
    >>> freestandingParenGroups("( ) (( )) (( )( ))")
    ["()", "(())", "(()())"]
     */
    public List<String> freestandingParenGroups(String paren_string) {
        // Create an array to hold the group information.
        int [] group_info = new int[paren_string.length()];
        int open_paren = 0;
        for (int i = 0; i < group_info.length; i++) {
            if (paren_string.charAt(i) == '(') {
                open_paren++;
            } else if (paren_string.charAt(i) == ')') {
                open_paren--;
            }
            if (open_paren < 0) {
                group_info[i] = -1;
            } else {
                group_info[i] = open_paren;
            }
        }
        List<String> output = new ArrayList<>();
        dfs(paren_string, group_info, 0, output);
        return output;
    }

    private void dfs(String paren_string, int [] group_info, int level, List<String> output) {
        if (level == paren_string.length()) {
            StringBuilder sb = new StringBuilder();
            for (int i = 0; i < level; i++) {
                sb.append(paren_string.charAt(i));
            }
            output.add(sb.toString());
            return;
        }
        for (int i = 0; i < group_info.length; i++) {
            if (group_info[i] == level) {
                dfs(paren_string, group_info, level + 1, output);
            }
        }
    }
