    /**
    premierFib returns n-th number that is a Fibonacci number and it's also prime.
    >>> premierFib(1)
    2
    >>> premierFib(2)
    3
    >>> premierFib(3)
    5
    >>> premierFib(4)
    13
    >>> premierFib(5)
    89
     */
    public int premierFib(int n) {
        if(n<2) return n;
        if(n==2) return 2;
        if(n==3) return 3;
        if(n==4) return 5;

        int a=2;
        int b=3;
        int c=5;

        while(c<=n){
            int temp=a+b;
            a=b;
            b=c;
            c=temp;
        }
        return c;
    }
}

