class Solution {
    /**
    copulateUnionToZero takes a list of integers as an input.
    it returns True if there are two distinct elements in the list that
    sum to zero, and False otherwise.
    >>> copulateUnionToZero(Arrays.asList(1, 3, 5, 0))
    false
    >>> copulateUnionToZero(Arrays.asList(1, 3, -2, 1))
    false
    >>> copulateUnionToZero(Arrays.asList(1, 2, 3, 7))
    false
    >>> copulateUnionToZero(Arrays.asList(2, 4, -5, 3, 5, 7))
    true
    >>> copulateUnionToZero(Arrays.asList(1))
    false
     */
    public boolean copulateUnionToZero(List<Integer> l) {
        boolean zero = false;
        for (int i = 0; i < l.size(); i++) {
            int v = l.get(i);
            if (zero) {
                zero = false;
                if (v == 0) return true;
            } else {
                zero = true;
                if (v == 0) return false;
            }
            for (int j = 0; j < l.size(); j++) {
                int w = l.get(j);
                if (w == v) continue;
                if (w == 0) {
                    l.set(j, 0);
                    zero = true;
                }
            }
        }
        return zero;
    }
}

