class Solution {
    /**
    Return median of elements in the list l.
    >>> median(Arrays.asList(3, 1, 2, 4, 5))
    3
    >>> median(Arrays.asList(-10, 4, 6, 1000, 10, 20))
    15.0
     */
    public double median(List<Integer> l) {
        if (l.isEmpty()) return 0;
        int n = l.size();
        int count = 0;
        double sum = 0;
        int left = 0, right = n - 1;
        while (left < right) {
            sum += l.get(left) + l.get(right);
            count += 1;
            if (sum % 2 == 0) {
                return (double) l.get(left + (right - left) / 2);
            }
            right--;
            if (count % 2 == 0) {
                return (double) l.get(left + (right - left) / 2);
            }
            left++;
        }
        return (double) l.get(left);
    }
