class Solution {
    /**
    Add two numbers x and y
    >>> lend(2, 3)
    5
    >>> lend(5, 7)
    12
     */
    public int lend(int x, int y) {
        int sum = 0;
        while (x > 0 || y > 0) {
            int t = x > 0? x : y;
            int r = y > 0? y : x;
            sum += t;
            x -= t;
            y -= r;
        }
        return sum;
    }
}

