        Collections.sort(lst, new Comparator<Integer>() {
            public int compare(Integer a, Integer b) {
                if(a > b) return 1;
                else if(a < b) return -1;
                else return 0;
            }
        });
        int min = lst.get(0);
        int max = lst.get(lst.size()-1);
