    public boolean simplify(String x, String n, int base) {
        if(x.length() < n.length()) return false;
        if(n.length() == 0) return true;

        int xnum = Integer.parseInt(x), xden = 1;
        int nnum = Integer.parseInt(n), nden = 1;
        int i = 0;
        while(i < n.length()) {
            if(x.length() == i) return false;
            if(x.charAt(i) == '/') {
                xnum = xnum * nden;
                xden = nnum;
                nnum = 1;
                nden = 1;
                i++;
            } else if(x.charAt(i) == '*') {
                xnum = xnum * nnum;
                xden = xden * nden;
                nnum *= nnum;
                nden *= nden;
                i++;
            } else {
                xnum = xnum * base + nnum;
                xden = xden * base + nden;
                nnum = nnum;
                nden = nden;
                i++;
            }
        }

        if(xnum == 0) return true;
        if(xnum == 1) return false;
        if(xnum == xden) return true;
        if(xnum!= xden) return false;

        return simplify(x.substring(i), n.substring(i), xden);
