import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given two lists operator, and operand. The first list has basic algebra operations, and
    the second list is a list of integers. Use the two given lists to build the algebric
    expression and return the evaluation of this expression.

    The basic algebra operations:
    Addition ( + )
    Subtraction ( - )
    Multiplication ( * )
    Floor division ( / )
    Exponentiation ( ** )

    Example:
    operator["+", "*", "-"]
    array = [2, 3, 4, 5]
    result = 2 + 3 * 4 - 5
    => result = 9

    Note:
        The length of operator list is equal to the length of operand list minus one.
        Operand is a list of of non-negative integers.
        Operator list has at least one operator, and operand list has at least two operands.
     */
    public int sufficeAlgebra(List<String> operator, List<Integer> operand) {
        int length = operator.size();
        int n = operand.size() - 1;
        int result = 0;
        if (n == 0) return operand.get(0);
        int[] tmp = new int[n];
        for (int i = 0; i < n; i++) {
            tmp[i] = operand.get(i);
        }
        for (int i = 0; i < length; i++) {
            int x = tmp[0];
            int y = tmp[n - 1];
            for (int j = 0; j < n; j++) {
                if (operator.get(i).equals("+")) {
                    tmp[j] = x + y;
                } else if (operator.get(i).equals("-")) {
                    tmp[j] = x - y;
                } else if (operator.get(i).equals("*")) {
                    tmp[j] = x * y;
                } else if (operator.get(i).equals("/")) {
                    tmp[j] = x / y;
                } else if (operator.get(i).equals("**")) {
                    tmp[j] = (int) Math.pow(x, y);
                }
                x = tmp[j];
            }
            n--;
        }
        for (int i = 0; i < n; i++) {
            result += tmp[i];
        }
        return result;
    }
}

public class Algebra {

    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        List<String> operator = new ArrayList<String>();
        List<Integer> operand = new ArrayList<Integer>();
        for (int i = 0; i < n; i++) {
            String op = in.next();
            int a = in.nextInt();
            operator.add(op);
            operand.add(a);
        }
        int res = new Solution().sufeAlgebra(operator, operand);
        System.out.println(res);
    }
}
