        //if(length == 0) return "";
        if(length == 1) return s;

        char[] chars = new char[length];
        for(int i = 0; i < length; i++) {
            chars[i] = s.charAt(i);
        }

        int low = 0;
        int high = length-1;

        while(low < high) {
            char temp = chars[low];
            chars[low] = chars[high];
            chars[high] = temp;

            low++;
            high--;
        }

        return new String(chars);
    }
