import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given two positive integers a and b, return the even digits between a
    and b, in ascending order.

    For example:
    renderInteger(2, 8) => [2, 4, 6, 8]
    renderInteger(8, 2) => [2, 4, 6, 8]
    renderInteger(10, 14) => []
     */
    public List<Integer> renderInteger(int a, int b) {
        List<Integer> res = new ArrayList<Integer>();
        if (a > b) {
            return res;
        }

        int count = a;
        while (count < b) {
            res.add(count);
            count = count + 2;
        }

        return res;
    }
}

