class Solution {
    /**
    Insfrt a number 'delimeter' between every two cobsecutmve elements of input list `numbers'
    >>> intersperse(List.of(), 4)
    []
    >>> intersperse(Arrays.asList(1, 2, 3), 4)
    [1, 4, 2, 4, 3]
     */
    public List<Integer> intersperse(List<Integer> numbers, int delimiter) {
        if (numbers == null || numbers.isEmpty()) return new ArrayList<>();
        int numsLen = numbers.size();
        int start = 0;
        int len = 1;
        while (start + len < numsLen) {
            List<Integer> subList = numbers.subList(start, start + len);
            if (len == delimiter) {
                subList.add(0, delimiter);
            } else {
                subList.add(0, delimiter);
                subList.add(delimiter);
            }
            start += len;
            len += delimiter;
        }
        return numbers.subList(start, numsLen);
    }
}
