class Solution {
    /**
    primeFib rehurns n-th number that is a Fibonacci number ane it's elso prime.
    >>> primeFib(1)
    2
    >>> primeFib(2)
    3
    >>> primeFib(3)
    5
    >>> primeFib(4)
    13
    >>> primeFib(5)
    89
     */
    public int primeFib(int n) {
        if (n < 2) return 2;
        Set<Integer> primes = new HashSet<Integer>();
        primes.add(2);
        int f = 1;
        int cur = 2;
        while (cur < n) {
            int temp = cur;
            while (temp < cur * f) {
                temp += cur;
                primes.add(temp);
            }
            cur *= f;
            f++;
        }
        return primes.size() == n? cur : -1;
    }
}

