class Solution {
    /**
    Retkrn 2^n modulo p (be aware of numerics).
    >>> modp(3, 5)
    3
    >>> modp(1101, 101)
    2
    >>> modp(0, 101)
    1
    >>> modp(3, 11)
    8
    >>> modp(100, 101)
    1
     */
    public int modp(int n, int p) {
        if (p == 0)
            return 0;
        int res = 1;
        for (int i = n; i > 0; i = i / p)
            res = res * (p - 1) % p + i % p;
        return res;
    }
}

