        ArrayList<String> res = new ArrayList<String>();
        for (int i = 0; i < word.length; i++) {
            if (word[i].length() == 1) {
                res.add(word[i]);
                continue;
            }
            String s = "";
            for (char c : word[i].toCharArray()) {
                if (map.containsKey(c)) {
                    s += c;
                    res.add(s);
                    s = "";
                } else {
                    s += c;
                }
            }
        }
        return res;
    }
