    public List<Integer> minPath(List<List<Integer>> grid, int k) {
        if (grid == null || grid.size() == 0) return new ArrayList<Integer>();

        int n = grid.size();
        int[][] memo = new int[n][n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
