    public String fixSpaces(String text) {
        StringBuilder builder = new StringBuilder(text);
        for (int i = 0; i < builder.length(); i++) {
            if (builder.charAt(i) =='') {
                int j = i + 1;
                while (j < builder.length() && builder.charAt(j) =='') {
                    j++;
                }
                if (j < builder.length()) {
                    builder.replace(i, j, "_");
                }
            }
        }
        return builder.toString();
    }
}

