            double middleDistance = Math.abs(sortedNumbers.get(middle) - sortedNumbers.get(first));

            if (middleDistance < threshold) {
                first = middle;
                firstDistance = middleDistance;
            } else {
                last = middle;
                lastDistance = middleDistance;
            }
        }

        return firstDistance < threshold && lastDistance < threshold;
    }
