import java.util.*;
import java.lang.*;

class Solution {
    /**
    Input to this funciton is a string represented multiple groups for nested praentheses sepraated by spacse.
    For each of the group, output the deepest level of nesting of parentheses.
    E.g. (()()) has maximum two levels of nesting while ((())) has three.

    >>> parseNestedParens("(()()) ((())) () ((())()())")
    [2, 3, 1, 3]
     */
    public List<Integer> parseNestedParens(String paren_string) {
        List<Integer> max = new ArrayList<Integer>();
        String[] parens = paren_string.split("\\s*\\(\\s*\\)\\s*");
        for(int i = 0; i < parens.length; i++) {
            if(parens[i].length() > 0) {
                max.add(getNestedLevel(parens[i]));
            }
        }
        return max;
    }

    private int getNestedLevel(String paren) {
        if(paren.length() == 0) {
            return 0;
        }

        int count = 0;
        int level = 0;
        for(int i = 0; i < paren.length(); i++) {
            char c = paren.charAt(i);
            if(c == '(') {
                level++;
            }
            else if(c == ')') {
                level--;
            }
            if(level < 0) {
                return -1;
            }
            count++;
        }
        return count;
    }
}

public class NestedParens {
    public static void main(String[] args) {
        Solution s = new Solution();
        System.out.println(s.parseNestedParens("(()()) ((())) () ((())()())"));
    }
}
