     */
    public int primeFib(int n) {
        if (n < 1) {
            return 0;
        }

        if (n == 1) {
            return 2;
        }

        int prev = 2;
        int curr = 3;
        int res = 2;

        while (curr <= n) {
            res += prev;
            prev = curr;
            curr = curr + prev;
        }

        return res;
    }
}

