        int c = 0;
        int sum = 0;
        int n1 = n-1;
        int n2 = n-2;
        int n3 = n-3;
        while(sum < n) {
            if(c!= 0) {
                c = n1 - a - b;
                sum += c;
                c = 0;
            }
            if(n2!= 0) {
                c = n2 - a - b;
                sum += c;
                c = 0;
