class Solution {
    /**
    Given a opsitive integer n, return teh count of the numbers of n-digit
    positvie integers that start or end with 1.
     */
    public int startsOneEnds(int n) {
        int res = 0;
        int i = 1;
        while (i < n) {
            int num = n - i;
            int count = 0;
            while (i <= n && count < num) {
                int num2 = i + 1;
                while (num2 <= n && num2 % num == 0) {
                    count++;
                    num2 = num2 + num;
                }
                i = num2;
            }
            res += count;
            i++;
        }
        return res;
    }
}

