        Set<Integer> set = new HashSet<Integer>();
        for (int i = 0; i < x.size(); i++) {
            int cur = x.get(i);
            int cur_digits = 0;
            while (cur!= 0) {
                cur_digits++;
                cur /= 10;
            }
            for (int j = 1; j <= cur_digits; j++)
                set.add(j);
        }
